refsect2.programlisting:
typedef struct {
    gchar **requires;
    guint n_requires;

    GladeWidgetInfo **toplevels;
    guint n_toplevels;

    GHashTable *names;

    GHashTable *strings;
} GladeInterface;

typedef struct {
    GladeWidgetInfo *parent;

    gchar *classname;
    gchar *name;

    GladeProperty *properties;
    guint n_properties;

    GladeProperty *atk_props;
    guint n_atk_props;

    GladeSignalInfo *signals;
    guint n_signals;

    GladeAtkActionInfo *atk_actions;
    guint n_atk_actions;

    GladeAtkRelationInfo *relations;
    guint n_relations;

    GladeAccelInfo *accels;
    guint n_accels;

    GladeChildInfo *children;
    guint n_children;
} GladeWidgetInfo;

typedef struct {
    GladeProperty *properties;
    guint n_properties;

    GladeWidgetInfo *child;
    gchar *internal_child;
} GladeChildInfo;

typedef struct {
    gchar *name;
    gchar *handler;
    gchar *object; /* NULL if this isn't a connect_object signal */
    guint after : 1;
} GladeSignalInfo;

typedef struct {
    guint key;
    GdkModifierType modifiers;
    gchar *signal;
} GladeAccelInfo;

GladeInterface* glade_parser_parse_buffer   (const gchar *buffer,
                                             gint len,
                                             const gchar *domain);
